package JavaPractice;

public class Swap2Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number1=10;
		int number2=20;
		System.out.println("\nIntially the numbers are :\n number1=> "+number1+"\n number2=> "+number2);
		int tmp=number1;
		number1=number2;
		number2=tmp;
		System.out.println("\nNow SWAPPING NUMBERS\n");
		System.out.println("\nAfter swappping numbers are :\n number1=> "+number1+"\n number2=> "+number2);
		

	}

}
